function converthumb(img,s,w,h,url,tit){
if(Number(s) > 2000){s = 2000;}
img = img.replace(/s72-c/gi,"s"+s+"");
if(url != "" || tit != ""){data = '<img class="album-img" title="'+tit+'" src="'+img+'" width="'+w+'" height="'+h+'"/>';}
else{data = '<a href="'+url+'" rel="'+tit+'"><img class="album-img" title="'+tit+'" src="'+img+'" width="'+w+'" height="'+h+'"/></a>';}
document.write(data);}
